import CozmoxSDK from '@cozmox-nebula/typescript-sdk';

const client = new CozmoxSDK({
  apiKey: process.env['COZMOX_API_KEY'], // This is the default and can be omitted
});

async function main() {
  const response = await client.nebula.hello();

  console.log(response.message);
}

main();

import CozmoxSDK from '@cozmox-nebula/typescript-sdk';

const client = new CozmoxSDK({
  apiKey: process.env['COZMOX_API_KEY'], // This is the default and can be omitted
});

async function main() {
  const response = await client.outbound.createCall({
    agentId: 'cx_xxxxxx',
    extras: { Name: 'Liza', current_datetime: '2025-01-01 10:10:10' },
    fromPhone: '+1234567890',
    toPhone: '+1234567890',
  });

  console.log(response.callId);
}

main();

